import {withStyles} from '@material-ui/styles'

const styles={
    root:{
        backgroundColor:"white",
        // borderRadius:"5px",
        padding:"0.5rem",
        position:"relative",
        overflow:"hidden",
        border:"1px solid black",
        "&:hover":{
            cursor:"pointer"
        }
    },
    colors:{
        // backgroundColor:"grey",
        height:"150px",
        width:"100%"
    },
    title:{
        display:"flex",
        justifyContent:"space-between",
        alignItems:"center",
        margin:"0",
        color:"black",
        paddingTop:"0.5rem",
        fontSize:"1rem",
        position:"relative"
    },
    emoji:{
        marginLeft:"0.5rem",
        fontSize:"1.5rem"
    },
    miniColor:{
        height:"25%",
        width:"20%",
        display:"inline-block",
        margin:"0 auto",
        position:"relative",
        marginBottom:"-3.9px"
    }
}

function MiniPalette(props){
    const {classes} = props;
    const miniColorBoxes = props.colors.map(color => (
        <div className={classes.miniColor} style={{backgroundColor: color.color}} 
        key={color.name}
        />
    ))
    return (
        <div className={classes.root}>
            <div className={classes.colors}>
                {miniColorBoxes}
            </div>
            <h5 className={classes.title}>{props.paletteName}
            <span className={classes.emoji}>{props.emoji}</span></h5>
        </div>
    );
};

export default withStyles(styles)(MiniPalette);
