import './colorBox.css';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import GrowModal from './growModal';

function ColorBox(props){

    function growHandler(){
        <GrowModal clrs={props.background}/>
    }
 return (
     <CopyToClipboard text={props.background}>
         <div style={{background: props.background}} className="ColorBox">
         <div className='copy-container'>
         <div className='box-content'>
            <span>{props.name}</span>
         </div>
         <button className='copy-button' onClick={growHandler}>Copy</button>
         </div>
         <span className='more'>MORE</span>
     </div>
     </CopyToClipboard>
 )
}

export default ColorBox;
