import {withStyles} from '@material-ui/styles'

const styles={
    root:{
        height:"100%",
        backgroundColor:"white",
        borderRadius:"10px",
        padding:"0.5rem",
        position:"relative",
        overflow:"hidden",
        border:"1px solid black",
        "&:hover":{
            cursor:"pointer"
        },
        marginBottom:"10px"
    },
    colors:{
        backgroundColor:"#dae1e4",
        height:"150px",
        width:"100%",
        borderRadius:"10px",
        overflow:"hidden"
    },
    title:{
        display:"flex",
        justifyContent:"space-between",
        alignItems:"center",
        margin:"0",
        color:"black",
        paddingTop:"0.5rem",
        fontSize:"0.9rem",
        position:"relative"
    },
    emoji:{
        marginLeft:"0.5rem",
        fontSize:"1.2rem"
    },
    miniColor:{
        height:"25%",
        width:"20%",
        display:"inline-block",
        margin:"0 auto",
        position:"relative",
        marginBottom:"-4.5px"
    }
}

function MiniPalette(props){
    const {classes} = props;
    const miniColorBoxes = props.colors.map(color => (
        <div className={classes.miniColor} style={{backgroundColor: color.color}} 
        key={color.name}
        />
    ))
    return (
        <div className={classes.root}>
            <div className={classes.colors}>
                {miniColorBoxes}
            </div>
            <h5 className={classes.title}>{props.paletteName}
            <span className={classes.emoji}>{props.emoji}</span></h5>
        </div>
    );
};

export default withStyles(styles)(MiniPalette);
