import './palette.css';
import ColorBox from "./colorBox";
import {useState} from 'react';
import NavBar from './navBar.js';

function Palette(props){

    const [level, setLevel] = useState(600);
    const [format, setFormat] = useState("hex")

    function ChangeLevel(newlevel){
        setLevel(newlevel);
        console.log(newlevel);
    }
    
    function handleChange(e){
        setFormat(e)
    }
    const ColorBoxes = props.palettes.colors[level].map(color => (
        <ColorBox background={color[format]} name={color.name} key={color.id} id={color.id} paletteId={props.palettes.id}/>
        
    ));

    return (
        <div className="palette">
            <NavBar changeColor={ChangeLevel} lev={level} handleChanges={handleChange}/>
            <div className="palette-colors">
                {ColorBoxes}
            </div>
            <footer className='footer'>
                {props.palettes.paletteName}
                <span>{props.palettes.emoji }</span>
            </footer>
        </div>
    )
}

export default Palette;
